/*
 * @title: createUniqueString
 * @description: 创建唯一字符串
 * @source: 未知
 */
export function createUniqueString() {
  const timestamp = +new Date() + "";
  const randomNum = parseInt((1 + Math.random()) * 65536) + "";
  return (+(randomNum + timestamp)).toString(32);
}

/*
 * @title: encodePwd
 * @description: 密码加密
 * @source: 未知
 */
export function encodePwd(str) {
  let out = "";
  for (let c of str) {
    out += c.charCodeAt(0) - 23;
  }
  return out;
}

/*
 * @title: reset
 * @description: 重置css样式
 * @source: 未知
 */
body {
  line-height: 1.4;
  color: #333;
  font-family: arial;
  font-size: 12px;
  background: white;
}
